(1513, 'es', 1, 'virusfoundlateradmin', 'Attention administrator! A file that was uploaded on {$a->date} with the filename {$a->filename} for the course {$a->course} by the user {$a->user} has since been found to contain a virus.  Here is a summary of what has happened to the file:\n\n{$a->action}\n\nThe user has also been notified.', '¡Atención, administrador! Un archivo subido el {$a->date} denominado {$a->filename} en el curso {$a->course} por el usuario {$a->user} contiene un virus. He aquí un resumen de lo que ha ocurrido con el archivo:\n\n{$a->action}\n\nEl usuario ha sido también avisado', NULL, 1295249667, NULL, 0, 0),
(1514, 'es', 1, 'virusfoundlateradminnolog', 'Attention administrator! A file that was uploaded with the filename {$a->filename} has since been found to contain a virus. Moodle was unable to resolve this file back to the user that originally uploaded it.\n\nHere is a summary of what has happened to the file:\n\n{$a->action}', '¡Atención, administrador! Un archivo subido denominado {$a->filename} contiene un virus. Moodole ha sido incapaz de devolver este archivo al usuario que lo subió.\n\nHe aquí un resumen de lo que ha ocurrido con el archivo:\n\n{$a->action}', NULL, 1295249667, NULL, 0, 0),
