(4886, 'en', 3, 'configcourserequestnotify2', 'Users who will be notified when a course is requested. Only users who can approve course requests are listed here.', 'Users who will be notified when a course is requested. Only users who can approve course requests are listed here.', NULL, 1295249996, NULL, 0, 0),
(4887, 'en', 3, 'configcoursesperpage', 'Enter the number of courses to be display per page in a course listing.', 'Enter the number of courses to be display per page in a course listing.', NULL, 1295249996, NULL, 0, 0),
(4888, 'en', 3, 'configcreatornewroleid', 'This role is automatically assigned to creators in new courses they created. This role is not assigned if creator already has needed capabilities in parent context.', 'This role is automatically assigned to creators in new courses they created. This role is not assigned if creator already has needed capabilities in parent context.', NULL, 1295249996, NULL, 0, 0),
(4889, 'en', 3, 'configcronclionly', 'If this is set, then the cron script can only be run from the command line instead of via the web.  This overrides the cron password setting below.', 'If this is set, then the cron script can only be run from the command line instead of via the web.  This overrides the cron password setting below.', NULL, 1295249996, NULL, 0, 0),
(4890, 'en', 3, 'configcronremotepassword', 'This means that the cron.php script cannot be run from a web browser without supplying the password using the following form of URL:<pre>\n    http://site.example.com/admin/cron.php?password=opensesame\n</pre>If this is left empty, no password is required.', 'This means that the cron.php script cannot be run from a web browser without supplying the password using the following form of URL:<pre>\n    http://site.example.com/admin/cron.php?password=opensesame\n</pre>If this is left empty, no password is required.', NULL, 1295249996, NULL, 0, 0),
(4891, 'en', 3, 'configcurlcache', 'Time-to-live for cURL cache, in seconds.', 'Time-to-live for cURL cache, in seconds.', NULL, 1295249996, NULL, 0, 0),
(4892, 'en', 3, 'configcustommenuitems', 'You can configure a custom menu here to be shown by themes. Each line consists of some menu text, a link URL (optional) and a tooltip title (optional), separated by pipe characters. You can specify a structure using hyphens. For example:\n<pre>\nMoodle community|http://moodle.org\n-Moodle free support|http://moodle.org/support\n-Moodle development|http://moodle.org/development\n--Moodle Tracker|http://tracker.moodle.org\n--Moodle Docs|http://docs.moodle.org\n-Moodle News|http://moodle.org/news\nMoodle company\n-Moodle commercial hosting|http://moodle.com/hosting\n-Moodle commercial support|http://moodle.com/support\n</pre>', 'You can configure a custom menu here to be shown by themes. Each line consists of some menu text, a link URL (optional) and a tooltip title (optional), separated by pipe characters. You can specify a structure using hyphens. For example:\n<pre>\nMoodle community|http://moodle.org\n-Moodle free support|http://moodle.org/support\n-Moodle development|http://moodle.org/development\n--Moodle Tracker|http://tracker.moodle.org\n--Moodle Docs|http://docs.moodle.org\n-Moodle News|http://moodle.org/news\nMoodle company\n-Moodle commercial hosting|http://moodle.com/hosting\n-Moodle commercial support|http://moodle.com/support\n</pre>', NULL, 1295249996, NULL, 0, 0),
(4893, 'en', 3, 'configdbsessions', 'If enabled, this setting will use the database to store information about current sessions.  This is especially useful for large/busy sites or sites built on cluster of servers.  For most sites this should probably be left disabled so that the server disk is used instead.  Note that changing this setting now will log out all current users (including you). If you are using MySQL please make sure that ''max_allowed_packet'' in my.cnf (or my.ini) is at least 4M.', 'If enabled, this setting will use the database to store information about current sessions.  This is especially useful for large/busy sites or sites built on cluster of servers.  For most sites this should probably be left disabled so that the server disk is used instead.  Note that changing this setting now will log out all current users (including you). If you are using MySQL please make sure that ''max_allowed_packet'' in my.cnf (or my.ini) is at least 4M.', NULL, 1295249996, NULL, 0, 0),
(4894, 'en', 3, 'configdebug', 'If you turn this on, then PHP''s error_reporting will be increased so that more warnings are printed.  This is only useful for developers.', 'If you turn this on, then PHP''s error_reporting will be increased so that more warnings are printed.  This is only useful for developers.', NULL, 1295249996, NULL, 0, 0),
(4895, 'en', 3, 'configdebugdisplay', 'Set to on, the error reporting will go to the HTML page. This is practical, but breaks XHTML, JS, cookies and HTTP headers in general. Set to off, it will send the output to your server logs, allowing better debugging. The PHP setting error_log controls which log this goes to.', 'Set to on, the error reporting will go to the HTML page. This is practical, but breaks XHTML, JS, cookies and HTTP headers in general. Set to off, it will send the output to your server logs, allowing better debugging. The PHP setting error_log controls which log this goes to.', NULL, 1295249996, NULL, 0, 0),
