(4907, 'en', 3, 'configdenyemailaddresses', 'To deny email addresses from particular domains list them here in the same way.  All other domains will be accepted. To deny subdomains add the domain with a preceding ''.''. eg <strong>hotmail.com yahoo.co.uk .live.com</strong>', 'To deny email addresses from particular domains list them here in the same way.  All other domains will be accepted. To deny subdomains add the domain with a preceding ''.''. eg <strong>hotmail.com yahoo.co.uk .live.com</strong>', NULL, 1295249997, NULL, 0, 0),
(4908, 'en', 3, 'configdigestmailtime', 'People who choose to have emails sent to them in digest form will be emailed the digest daily. This setting controls which time of day the daily mail will be sent (the next cron that runs after this hour will send it).', 'People who choose to have emails sent to them in digest form will be emailed the digest daily. This setting controls which time of day the daily mail will be sent (the next cron that runs after this hour will send it).', NULL, 1295249997, NULL, 0, 0),
